function reset(){for(let e=0;e<sliderImages.length;e++)sliderImages[e].style.display="none"}function startSlide(){reset(),sliderImages[0].style.display="flex"}function slideLeft(){reset(),sliderImages[current-1].style.display="flex",current--}function slideRight(){reset(),sliderImages[current+1].style.display="flex",current++}let sliderImages=document.querySelectorAll(".slider__wrapper"),leftArrow=document.querySelector(".arrow--left"),rightArrow=document.querySelector(".arrow--right"),current=0;leftArrow.addEventListener("click",function(){0===current&&(current=sliderImages.length),slideLeft()}),rightArrow.addEventListener("click",function(){current===sliderImages.length-1&&(current=-1),slideRight()}),startSlide();